#import "BKRunBlockAtDealloc.h"

#import "BKMacrosDefinitions.h"

////////////////////////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////////////////////////
@implementation BKRunBlockAtDealloc

@synthesize block = _block;


////////////////////////////////////////////////////////////////////////////////////////////////////
- (id)initWithBlock:(BKBasicBlock)block {
    self = [super init];
    if (self) {
        self.block = block;
    }
    return self;
}


@end
